      &quot;8.8.8.8&quot;,
      &quot;8.8.4.4&quot;
    ]
  },
  &quot;routing&quot;: {
    &quot;strategy&quot;: &quot;rules&quot;,
    &quot;settings&quot;: {
      &quot;rules&quot;: [
        {
          &quot;type&quot;: &quot;field&quot;,
          &quot;domain&quot;: [
